import React, { useEffect, useState } from "react"

const StepsComponent = ({}) => {
    const [quizSteps, setQuizSteps] = useState([
        {
            title: "question 1",
            type: "text",
            answers: [
                "answer 1",
                "answer 2",
                "answer 3"
            ],
        },
        {
            title: "question 2",
            type: "slider",
            answers: [
                "answer 1",
                "answer 2",
                "answer 3"
            ],
        },
    ])
    const inputChange = (event) => {
    }
    const answerChange = (event) => {


        setQuizSteps((prevState) => {
            let value = event.target.value
            let stepIndex = parseInt(event.target.getAttribute('indexStep'))
            let answerIndex = parseInt(event.target.getAttribute('indexAnswer'))
            let newQuizSteps = Array.from(prevState)
            newQuizSteps[stepIndex].answers[answerIndex] = value
            return newQuizSteps
        })
    }

    const questionChange = (event) => {
        let value = event.target.value
        let stepIndex = parseInt(event.target.getAttribute('indexStep'))
        let newQuizSteps = quizSteps
        newQuizSteps[stepIndex].title = value
        console.log(newQuizSteps)
        setQuizSteps(newQuizSteps)
    }
    useEffect(() => console.log(quizSteps), [quizSteps])
    return (<div>
        <div class="text-center mb-4">
            <h5>Шаги</h5>
            <p class="card-title-desc">Заполните шаги (или оставьте стандартные)</p>
        </div>

            {
                quizSteps.map((step, i) => {
                    return <React.Fragment><div>
                                <div class="row">
                                    <div class="mb-3">
                                        Шаг {i+1} / {quizSteps.length}
                                    </div>
                                </div>
                                <div class="row">
                                    <div class="col-lg-6">
                                        <div class="mb-3">
                                            <label for="basicpill-servicetax-input" class="form-label">Текст вопроса</label>
                                            <input type="text" indexStep={i} onChange={questionChange} class="form-control" name="mail" id="basicpill-servicetax-input" value={step.title} />
                                        </div>
                                    </div>
                                    <div class="col-lg-6">
                                        <div class="mb-3">
                                            <label for="basicpill-servicetax-input" class="form-label">Тип шага</label>
                                            <select onChange={inputChange} className="form-select custom-select" id="template" name="template_id" name="qoopler" value={step.type} >
                                                    <option value="text">Текстовый</option>
                                                    <option value="slider">Слайдер</option>
                                            </select>
                                        </div>
                                    </div>
                                </div>
                                <div className="mb-3" style={{marginLeft:"15px"}}>
                                    <span>Ответы на вопрос</span>
                                </div>
                                {
                                step.answers.map((answer, index) => {
                                    return <div key={i} className="column">
                                                <div className="col-lg-6 ml-2">
                                                    <div className="mb-3" style={{display:"flex", marginLeft:"30px", alignItems:"end"}}>
                                                        {index+1}.  <input style={{marginLeft:"10px"}} type="text" onChange={answerChange} indexAnswer={index} indexStep={i} class="form-control" name="mail" id="basicpill-servicetax-input" value={answer} key={answer} />
                                                    </div>
                                                </div>
                                            </div>
                                })}
                                {

                                }

                            </div>
                            <hr class="my-4"></hr>
                        </React.Fragment>
                })
            }


        <div class="d-flex justify-content-center">
            <button href="javascript: void(0);" class="btn btn-primary" onclick="nextTab()"><i class="feather-plus"></i> Добавить шаг </button>
        </div>
        <ul class="pager wizard twitter-bs-wizard-pager-link">
            <li class="prev"><a href="javascript: void(0);" class="btn btn-primary" onclick="nextTab()"><i class="feather-arrow-left"></i> Назад </a></li>
            <li class="next"><a href="javascript: void(0);" class="btn btn-primary" onclick="nextTab()">Сохранить <i class="feather-check"></i></a></li>
        </ul>
    </div>)
}

export default StepsComponent
