                                <div className="mb-3">
                                  <label
                                    htmlFor="basicpill-servicetax-input"
                                    className="form-label"
                                  >
                                    Mail
                                  </label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    name="mail"
                                    onChange={inputChange}
                                    id="basicpill-servicetax-input"
                                    value={form.mail}
                                  />
                                </div>
                              </div>
                            </div>
                            <div className="row">
                              <div className="col-lg-6">
                                <div className="mb-3">
                                  <label
                                    htmlFor="basicpill-servicetax-input"
                                    className="form-label"
                                  >
                                    Marquiz
                                  </label>
                                  <input
                                    type="text"
                                    className="form-control"
                                    name="marquiz"
                                    onChange={inputChange}
                                    id="basicpill-servicetax-input"
                                    value={form.marquiz}
                                  />
                                </div>
                              </div>
                              <div className="col-lg-6">
                                <label
                                  htmlFor="basicpill-servicetax-input"
                                  className="form-label"
                                >
                                  Qoopler
                                </label>
                                <select
                                  onChange={inputChange}
                                  className="form-select custom-select"
                                  id="template"
                                  name="qoopler"
                                  value={form.qoopler == true ? '1' : '0'}
                                >
                                  <option value="0">Нет</option>
                                  <option value="1">Да</option>
                                </select>
                              </div>
                            </div>
                            <div className="row">
                              <div className="col-lg-6">
                                <label
                                  htmlFor="basicpill-servicetax-input"
                                  className="form-label"
                                >
                                  Roistat
                                </label>
                                <select
                                  onChange={inputChange}
                                  className="form-select custom-select"
                                  id="template"
                                  name="roistat"
                                  value={form.roistat == true ? '1' : '0'}
                                >
                                  <option value="0">Нет</option>
                                  <option value="1">Да</option>
                                </select>
                              </div>
                            </div>
                          </form>
                          <ul
                            className="pager wizard
                                                            twitter-bs-wizard-pager-link"
                          >
                            <li className="prev">
                              <a
                                onClick={prevStepClick}
                                className="btn btn-primary"
                              >
                                <i className="feather-arrow-left"></i> Назад{' '}
                              </a>
                            </li>
                            <li className="next">
                              <a
                                onClick={stepsSaveClick}
                                className="btn btn-primary"
                              >
                                Сохранить <i className="feather-check"></i>
                              </a>
                            </li>
                          </ul>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

class DomainEditPageContainer extends Component {
  render() {
    const { addDomainToList } = this.props;

    return <DomainEdit addDomainToList={addDomainToList} />;
  }
}

const mapStateToProps = ({}) => {
  return {};
};

const mapDispatchToProps = (dispatch, { apiService }) => {
  return bindActionCreators(
    {
      addDomainToList: addDomainToList,
    },
    dispatch
  );
};

export default compose(
  withApiService(),
  connect(mapStateToProps, mapDispatchToProps)
)(DomainEditPageContainer);
