const updateAuthStatus = (state, action) => {

    if (state === undefined) {
      return {
        auth: null,
        loading: false,
        error: null,
        phone: null,
        checked: false,
      };
    }

    switch (action.type) {
      case 'NOT_AUTHORIZED':
        return {
          auth: false,
          userID:null,
          objectID:null,
          loading: false,
          error: null,
          phone: null,
          checked: false,
      };
      case 'FETCH_LOGIN_REQUEST':
        return {
          auth: state.authStatus.auth,
          loading: true,
          userID:null,
          objectID:null,
          error: null,
          phone: null,
          checked: false,
      };
      case 'FETCH_LOGIN_ERROR':
        return {
          auth: false,
          loading: false,
          userID:null,
          objectID:null,
          error: action.payload,
          phone: null,
          checked: false,
        };

      case 'FETCH_LOGIN_SUCCESS':
        return {
          auth: true,
          loading: false,
          userID:null,
          objectID:null,
          error: null,
          phone: null,
          checked: false,
        };
      default:
        return state.authStatus;
    }
  };

  export default updateAuthStatus;
